<template>
  <nuxt-link :to="{ path: '/', query: $route.query }" class="jrinlab-logo">
    <img :src="imgSrc" :style="{ height: `${height}px` }" alt="JrinLab Logo" />
  </nuxt-link>
</template>

<script>
export default {
  name: 'AppLogo',
  props: {
    dark: {
      type: Boolean,
      default: false,
    },
    height: {
      type: [Number, String],
      default: 48,
    },
  },
  computed: {
    imgSrc() {
      return this.dark
        ? 'data:image/png;base64,'
        : 'data:image/png;base64,'
    },
  },
}
</script>

<style lang="scss" scoped>
.jrinlab-logo {
  display: inline-block;

  img {
    vertical-align: middle;
    max-height: 100%;
  }
}
</style>
